mapper.select:
SELECT CONECT_IP as conectIp, IF(CONECT_MTHD="I","login","logout") as loginType, CONECT_MTHD as conectMthd, LOGIN_YN as loginYn, DATE_FORMAT(CREAT_DT, '%Y-%m-%d %H:%i:%s') as creatDt FROM COMTNLOGINLOG WHERE CONECT_ID = #{loginId}
AND ${searchCondition} LIKE concat('%', #{searchKeyword}, '%')
ORDER BY CREAT_DT DESC

SELECT
	EMPLYR_ID as userId, LST_PWD_DT as lstPwdDt
FROM COMTNEMPLYRINFO
WHERE EMPLYR_ID = #{loginId}
AND PASSWORD = #{userPw}
